import pandas as pdimport numpy as npdata=pd.read_csv("matrix_rating_fix.csv")data_2=pd.DataFrame(index=np.arange(1,len(data.index)+1),columns=np.arange(1,len(data.index)+1))del data['Unnamed: 0']data.set_index('Unnamed: 0.1',inplace =True)for user in data.index:    for user_pembanding in data.index:        if user==user_pembanding:            continue        temp=0        a=[]        for film in data.columns:            if data.at[user,film] > 0 and data.at[user_pembanding,film] > 0:                temp+=1                a.append(film)        data_2.at[user,user_pembanding]=temp        print (user,">>",user_pembanding,":")data_2.to_csv('matrix_CRM.csv')