import pandas as pdimport numpy as npdata=pd.read_csv("matrix_rating_3.csv")data_2=pd.DataFrame(index=np.arange(1,944),columns=np.arange(1,944))del data['Unnamed: 0']del data['User']for user in range(943):    for user_pembanding in range(943):        if user==user_pembanding:            continue        temp=0        a=[]        for film in data.columns:            if data.at[user,film] > 0 and data.at[user_pembanding,film] > 0:                temp+=1                a.append(film)        data_2.at[user+1,user_pembanding+1]=temp        print (user+1,">>",user_pembanding+1,":",temp,"<<<",a)data_2.to_csv('matrix_rating_4.csv')